--  DDL for View CANTIDADPACIENTES
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "ALEJANDRO"."CANTIDADPACIENTES" ("CIUDAD", "DEPARTAMENTO", "PAIS", "TOTAL") AS 
  SELECT CI.NOMBRE Ciudad, DE.NOMBRE Departamento, PS.NOMBRE Pais, count(PA.ID) Total
FROM PACIENTE PA 
JOIN PERSONA PE ON PA.ID=PE.ID 
JOIN CIUDAD CI ON PE.CIUDAD=CI.ID_CIUDAD 
JOIN DEPARTAMENTO DE ON CI.DEPARTAMENTO_ID_DEPARTAMENTO=DE.ID_DEPARTAMENTO
JOIN PAIS PS ON DE.ID_DEPARTAMENTO=PS.ID_PAIS GROUP BY ROLLUP(CI.NOMBRE, DE.NOMBRE, PS.NOMBRE, PA.ID);
--------------------------------------------------------
--  DDL for View DATOS_REPORTES_CARLOS_MARTINEZ
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "ALEJANDRO"."DATOS_REPORTES_CARLOS_MARTINEZ" ("CITA", "ID_MEDICO", "NOMBRE_MEDICO", "ID_PACIENTE", "NOMBRE_PACIENTE", "CIUDAD", "DEPARTAMENTO", "PAIS") AS 
  SELECT CM.ID CITA, M.ID ID_MEDICO, PA.NOMBRE||' '||PA.APELLIDO NOMBRE_MEDICO, P.ID ID_PACIENTE, PE.NOMBRE||' '||PE.APELLIDO NOMBRE_PACIENTE, C.NOMBRE CIUDAD, D.NOMBRE DEPARTAMENTO, PS.NOMBRE PAIS 
  FROM CITAS_MEDICAS CM JOIN MEDICO M ON M.ID = CM.MEDICO JOIN PERSONA PA ON PA.ID = M.ID JOIN PACIENTE P ON P.ID = CM.PACIENTE JOIN PERSONA PE ON PE.ID = P.ID JOIN CIUDAD C ON C.ID_CIUDAD = PE.CIUDAD
  JOIN DEPARTAMENTO D ON D.ID_DEPARTAMENTO = C.DEPARTAMENTO_ID_DEPARTAMENTO JOIN PAIS PS ON PS.ID_PAIS = D.PAIS_ID_PAIS;
--------------------------------------------------------
--  DDL for View ENTREGADOS
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "ALEJANDRO"."ENTREGADOS" ("EPS", "MEDICAMENTO", "ENTREGADOS") AS 
  SELECT E.NOMBRE EPS, ME.NOMBRE MEDICAMENTO, SUM(DM.CANTIDAD) ENTREGADOS
FROM EPS E JOIN PACIENTE P ON E.ID_EPS=P.EPS_ID_EPS JOIN CITAS_MEDICAS C ON P.ID=C.PACIENTE JOIN 
ORDEN_MEDICAMENTO OM ON OM.CITAS_MEDICAS=C.ID JOIN DETALLE_ORDENMEDICAMENTO DM ON DM.ORDEN_MEDICAMENTO=OM.ID JOIN MEDICAMENTO ME 
ON ME.ID_MEDICAMENTO=DM.MEDICAMENTO JOIN FARMACIA F ON F.ID_FARMACIA=ME.FARMACIA_ID_FARMACIA JOIN FARMACEUTICO FARMA ON F.FARMACEUTICO=FARMA.ID
WHERE DM.ESTADO=1
GROUP BY ROLLUP(E.NOMBRE, ME.NOMBRE);
--------------------------------------------------------
--  DDL for View INVENTARIO
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "ALEJANDRO"."INVENTARIO" ("NOMBRE", "FARMACIA", "CANTIDAD", "TOTAL") AS 
  SELECT M.NOMBRE, F.NOMBRE FARMACIA, M.CANTIDAD, SUM(M.CANTIDAD) TOTAL from MEDICAMENTO M  join FARMACIA F 
ON F.ID_FARMACIA=M.FARMACIA_ID_FARMACIA JOIN PERSONA P ON F.FARMACEUTICO=P.ID JOIN FARMACEUTICO FARMA ON P.ID=FARMA.ID where F.NOMBRE='La rebaja'
GROUP BY ROLLUP (M.NOMBRE, F.NOMBRE, M.CANTIDAD);
